// This file is only imported when the runtime is `cloudflare_workers_platform`.

import { Config } from "../../../mod.ts";
import { ActorDriver, CallOpts, CreateOpts, ExistsOpts, GetOrCreateAndCallOpts } from "../../driver.ts";

export class CloudflareDurableObjectsActorDriver implements ActorDriver {
	public constructor(public readonly config: Config) {}

	async createActor(opts: CreateOpts): Promise<void> {
		const stub = this.getStub(opts.moduleName, opts.actorName, opts.instanceName);
		return await stub.init({
			module: opts.moduleName,
			actor: opts.actorName,
			instance: opts.instanceName,
			input: opts.input,
			trace: opts.trace,
		});
	}

	async callActor(opts: CallOpts): Promise<unknown> {
		const stub = this.getStub(opts.moduleName, opts.actorName, opts.instanceName);

		// HACK: Fixes "Type instantiation is excessively deep and possibly infinite."
		return await stub.callRpc({
			fn: opts.fn,
			request: opts.request,
			trace: opts.trace,
		});
	}

	async getOrCreateAndCallActor(opts: GetOrCreateAndCallOpts): Promise<unknown> {
		const stub = this.getStub(opts.moduleName, opts.actorName, opts.instanceName);

		// HACK: Fixes "Type instantiation is excessively deep and possibly infinite."
		return await stub.getOrCreateAndCallRpc({
			init: {
				module: opts.moduleName,
				actor: opts.actorName,
				instance: opts.instanceName,
				input: opts.input,
				trace: opts.trace,
			},
			fn: opts.fn,
			request: opts.request,
			trace: opts.trace,
		});
	}

	async actorExists(opts: ExistsOpts): Promise<boolean> {
		const stub = this.getStub(opts.moduleName, opts.actorName, opts.instanceName);
		return await stub.initialized();
	}

	private getStub(
		moduleName: string,
		actorName: string,
		instanceName: string,
	): any {
		// TODO: Fix Deno.env.get hack. This does not return a string, it returns an object.
		const ns = Deno.env.get("__GLOBAL_DURABLE_OBJECT") as any;

		const module = this.config.modules[moduleName];
		const actor = module.actors[actorName];
		const name = `%%${module.storageAlias}%%${actor.storageAlias}%%${instanceName}`;
		const id = ns.idFromName(name);

		return ns.get(id);
	}
}

export { CloudflareDurableObjectsActorDriver as ActorDriver };

/// <reference types="npm:@cloudflare/workers-types" />
import { DurableObject } from "cloudflare:workers";
import { CloudflareDurableObjectsStorage } from "./storage.ts";
import { CloudflareDurableObjectsSchedule } from "./schedule.ts";
import { ActorBase } from "../../actor.ts";
import {
	Actor,
	ActorContext,
	appendTraceEntry,
	Config,
	Module,
	ModuleContextParams,
	Runtime,
	Trace,
} from "../../../mod.ts";
import { RegistryCallMap } from "../../../proxy.ts";
import { ActorDriver } from "./driver.ts";
import { newTrace } from "../../../trace.ts";
import { errorToLogEntries, log } from "../../../logger.ts";

const KEYS = {
	META: {
		MODULE: "meta:module",
		ACTOR: "meta:module",
		CREATED_AT: "__meta:created_at",
	},
	SCHEDULE: {
		SCHEDULE: "schedule:schedule",
		EVENT_PREFIX: "schedule:event:",
		event(id: string): string {
			return `${this.EVENT_PREFIX}${id}`;
		},
	},
	STATE: "state",
};

interface ScheduleState {
	// Sorted by timestamp asc
	events: ScheduleIndexEvent[];
}

interface ScheduleIndexEvent {
	timestamp: number;
	eventId: string;
}

interface ScheduleEvent {
	timestamp: number;
	fn: string;
	request: unknown;
}

// MARK: TODO:
interface InitOpts {
	module: string;
	actor: string;
	instance: string;
	input: any;
	trace: Trace;
	ignoreAlreadyInitialized?: boolean;
}

interface GetOrCreateAndCallOpts {
	init: InitOpts;
	fn: string;
	request: unknown;
	trace: Trace;
}

interface CallRpcOpts {
	fn: string;
	request: unknown;
	trace: Trace;
}

/*
 * __GlobalDurableObject type used for referencing an instance of the class.
 */
export interface __GlobalDurableObjectT extends DurableObject {
	init(opts: InitOpts): Promise<void>;
	initialized(): Promise<boolean>;
	getOrCreateAndCallRpc(opts: GetOrCreateAndCallOpts): Promise<any>;
	callRpc({ fn, request }: CallRpcOpts): Promise<any>;
	scheduleEvent(timestamp: number, fn: string, request: unknown): Promise<void>;
	alarm(): Promise<void>;
	get storage(): DurableObjectStorage;
}

/**
 * Actor data & config read from the actor state.
 */
interface ActorMeta {
	moduleName: string;
	actorName: string;
	state: any;
}

/**
 * Generate a __GlobalDurableObject class that has access to the current config.
 *
 * We have to pass the config like this since the config's import is
 * dynamically generated in entrypoint.ts.
 *
 * Doing this instead of setting a static `__GlobalDurableObject.config = xxxx`
 * is better since it ensures that you _can't_ create an instance of
 * __GlobalDurableObject that doesn't have an associated config.
 */
export function buildGlobalDurableObjectClass(
	config: Config,
	dependencyCaseConversionMap: RegistryCallMap,
	actorDependencyCaseConversionMap: RegistryCallMap,
) {
	class __GlobalDurableObject extends DurableObject implements __GlobalDurableObjectT {
		private runtime: Runtime<ModuleContextParams>;

		constructor(ctx: DurableObjectState, env: unknown) {
			super(ctx, env);

			this.runtime = new Runtime(
				config,
				new ActorDriver(config),
				dependencyCaseConversionMap,
				actorDependencyCaseConversionMap,
			);
		}

		/**
		 * Reads the metadata related to this actor from storage.
		 *
		 * This data is set in `init`.
		 */
		async getMeta(): Promise<ActorMeta> {
			// Create actor instance
			const storageRes = await this.ctx.storage.get<string>([KEYS.META.MODULE, KEYS.META.ACTOR, KEYS.STATE]);
			const moduleName = storageRes.get(KEYS.META.MODULE);
			const actorName = storageRes.get(KEYS.META.ACTOR);
			const state = storageRes.get(KEYS.STATE);
			if (moduleName == undefined || actorName == undefined) throw new Error("actor not initialized");
			if (state == undefined) throw Error("actor state not initiated");

			return { moduleName, actorName, state };
		}

		// TODO: optimize to use in-memory state
		private async constructActor(meta: ActorMeta): Promise<ActorBase<unknown, unknown>> {
			// Get actor config
			if (!(meta.moduleName in config.modules)) throw new Error("module not found");
			const moduleConfig = config.modules[meta.moduleName];
			if (!(meta.actorName in moduleConfig.actors)) throw new Error("actor not found");
			const actorConfig = moduleConfig.actors[meta.actorName];

			// TODO: cache actor instance in memory
			// TODO: use ctx.waitUntil for all calls
			// Run actor function
			const actor = new (actorConfig.actor)(
				new CloudflareDurableObjectsStorage(this),
				new CloudflareDurableObjectsSchedule(this),
			);

			return actor;
		}

		private createActorContext(moduleName: string, actorName: string, trace: Trace): ActorContext<ModuleContextParams> {
			// Build context
			const module = config.modules[moduleName];
			const context = new ActorContext<ModuleContextParams>(
				this.runtime,
				trace,
				moduleName,
				this.runtime.postgres.getOrCreatePrismaClient(this.runtime.config, module),
				module.db?.schema,
				actorName,
				dependencyCaseConversionMap,
				actorDependencyCaseConversionMap,
			);

			return context;
		}

		async init(opts: InitOpts) {
			// Check if already initialized
			if (await this.initialized()) {
				if (!opts.ignoreAlreadyInitialized) throw new Error("already initialized");
			}

			// Store metadata
			await this.ctx.storage.put({
				[KEYS.META.MODULE]: opts.module,
				[KEYS.META.ACTOR]: opts.actor,
				[KEYS.META.CREATED_AT]: Date.now(),
			});

			// Build initial state
			const actor = await this.constructActor({
				moduleName: opts.module,
				actorName: opts.actor,
				state: undefined,
			});
			const context = this.createActorContext(
				opts.module,
				opts.actor,
				appendTraceEntry(opts.trace, { actorInitialize: { module: opts.module, actor: opts.actor } }),
			);
			const state = actor.initialize(context, opts.input);
			await this.ctx.storage.put(KEYS.STATE, state);
		}

		async initialized() {
			return await this.ctx.storage.get(KEYS.META.MODULE) != undefined;
		}

		async getOrCreateAndCallRpc(opts: GetOrCreateAndCallOpts): Promise<any> {
			await this.init({
				...opts.init,
				ignoreAlreadyInitialized: true,
			});

			return await this.callRpc({ fn: opts.fn, request: opts.request, trace: opts.trace });
		}

		async callRpc({ fn, request, trace }: CallRpcOpts): Promise<any> {
			const meta = await this.getMeta();
			const actor = await this.constructActor(meta);
			actor.state = meta.state;

			const context = this.createActorContext(
				meta.moduleName,
				meta.actorName,
				appendTraceEntry(trace, { actorCall: { module: meta.moduleName, actor: meta.actorName, fn } }),
			);

			// Call fn
			let callRes = (actor as any)[fn](context, request);
			if (callRes instanceof Promise) callRes = await callRes;

			// Update state
			await this.ctx.storage.put(KEYS.STATE, actor.state);

			return callRes;
		}

		async scheduleEvent(timestamp: number, fn: string, request: unknown) {
			// Save event
			const eventId = crypto.randomUUID();
			await this.ctx.storage.put<ScheduleEvent>(KEYS.SCHEDULE.event(eventId), {
				timestamp,
				fn,
				request,
			});

			// Read index
			const schedule: ScheduleState = await this.ctx.storage.get(KEYS.SCHEDULE.SCHEDULE) ?? { events: [] };

			// Insert event in to index
			const newEvent: ScheduleIndexEvent = { timestamp, eventId };
			const insertIndex = schedule.events.findIndex((x) => x.timestamp > newEvent.timestamp);
			if (insertIndex === -1) {
				schedule.events.push(newEvent);
			} else {
				schedule.events.splice(insertIndex, 0, newEvent);
			}

			// Write new index
			await this.ctx.storage.put(KEYS.SCHEDULE.SCHEDULE, schedule);

			// Update alarm if:
			// - this is the newest event (i.e. at beginning of array) or
			// - this is the only event (i.e. the only event in the array)
			if (insertIndex == 0 || schedule.events.length == 1) {
				await this.ctx.storage.setAlarm(newEvent.timestamp);
			}
		}

		async alarm() {
			const now = Date.now();

			// Read index
			const scheduleIndex: ScheduleState = await this.ctx.storage.get(KEYS.SCHEDULE.SCHEDULE) ?? { events: [] };

			// Remove events from schedule
			const runIndex = scheduleIndex.events.findIndex((x) => x.timestamp > now);
			const scheduleIndexEvents = scheduleIndex.events.splice(0, runIndex + 1);

			// Find events to trigger
			const eventKeys = scheduleIndexEvents.map((x) => KEYS.SCHEDULE.event(x.eventId));
			const scheduleEvents = await this.ctx.storage.get<ScheduleEvent>(eventKeys);
			await this.ctx.storage.delete(eventKeys);

			// Write new schedule
			await this.ctx.storage.put(KEYS.SCHEDULE.SCHEDULE, scheduleIndex);

			// Set alarm for next event
			if (scheduleIndex.events.length > 0) {
				await this.ctx.storage.setAlarm(scheduleIndex.events[0].timestamp);
			}

			// Iterate by event key in order to ensure we call the events in order
			for (const eventKey of eventKeys) {
				const event = scheduleEvents.get(eventKey)!;
				try {
					// TODO: how do we handle this promise cleanly?
					const res = this.callRpc({ fn: event.fn, request: event.request, trace: newTrace({ actorSchedule: {} }) });
					if (res instanceof Promise) await res;
				} catch (err) {
					log("error", "failed to run scheduled event", ["fn", event.fn], ...errorToLogEntries("error", err));
				}
			}
		}

		/**
		 * Make storage publicly accessible for use in `StorageDriver`.
		 */
		public get storage(): DurableObjectStorage {
			return this.ctx.storage;
		}
	}

	return __GlobalDurableObject;
}

import { ScheduleDriver } from "../../driver.ts";
import { __GlobalDurableObjectT } from "./global_durable_object.ts";

export class CloudflareDurableObjectsSchedule implements ScheduleDriver {
	constructor(private readonly durableObject: __GlobalDurableObjectT) {}

	after(duration: number, fn: string, request: unknown): void {
		this.durableObject.scheduleEvent(Date.now() + duration, fn, request);
	}
	at(timestamp: number, fn: string, request: unknown): void {
		this.durableObject.scheduleEvent(timestamp, fn, request);
	}
}

import { StorageDriver } from "../../driver.ts";
import { __GlobalDurableObjectT } from "./global_durable_object.ts";

export class CloudflareDurableObjectsStorage implements StorageDriver {
	constructor(private readonly durableObject: __GlobalDurableObjectT) {}

	async get<V>(key: string): Promise<V | undefined> {
		const jsonRaw = await this.durableObject.storage.get<string>(buildStorageKey(key));
		if (jsonRaw) {
			return await JSON.parse(jsonRaw);
		} else {
			return undefined;
		}
	}
	async put<V>(key: string, value: V): Promise<void> {
		await this.durableObject.storage.put(key, JSON.stringify(value));
	}
	async delete(key: string): Promise<void> {
		await this.durableObject.storage.delete(buildStorageKey(key));
	}
}

/**
 * Build a key from the actor's API that's namespaced to the storage.
 *
 * This allows us to store metadata on different keys.
 */
function buildStorageKey(key: string): string {
	return `storage:${key}`;
}

// This file is only imported when the runtime is `deno`
//
import { ModuleContextParams } from "../../../context.ts";
import { ActorContext, appendTraceEntry, Config, Runtime, Trace, TraceEntry } from "../../../mod.ts";
import { RegistryCallMap } from "../../../proxy.ts";
import { ActorDriver, CallOpts, CreateOpts, ExistsOpts, GetOrCreateAndCallOpts } from "../../driver.ts";
import { MemorySchedule } from "./schedule.ts";
import { MemoryStorage } from "./storage.ts";

export interface ActorEntry {
	moduleName: string;
	actorName: string;
	instanceName: string;
	state?: string;
	storage: Map<string, string>;
}

export class MemoryActorDriver implements ActorDriver {
	private runtime: Runtime<ModuleContextParams>;
	private encoder = new TextEncoder();
	private actorRegistry = new Map<string, ActorEntry>();

	public constructor(
		public readonly config: Config,
		private dependencyCaseConversionMap: RegistryCallMap,
		private actorDependencyCaseConversionMap: RegistryCallMap,
	) {
		this.runtime = new Runtime(
			this.config,
			this,
			this.dependencyCaseConversionMap,
			this.actorDependencyCaseConversionMap,
		);
	}

	async createActor({ moduleName, actorName, instanceName, input, trace }: CreateOpts): Promise<void> {
		const id = await this.getId(moduleName, actorName, instanceName);

		// Ensure doesn't already exist
		if (this.actorRegistry.has(await this.getId(moduleName, actorName, instanceName))) {
			throw new Error("actor already created");
		}

		// Build drivers
		const actorEntry: ActorEntry = {
			moduleName,
			actorName,
			instanceName,
			state: undefined,
			storage: new Map(),
		};

		// TODO: cache init actor in memory
		// Run actor function
		const actor = this.constructActor(actorEntry, true);
		const context = this.createActorContext(
			moduleName,
			actorName,
			appendTraceEntry(trace, { actorInitialize: { module: moduleName, actor: actorName } }),
		);
		let initRes = actor.initialize(context, input);
		if (initRes instanceof Promise) initRes = await initRes;

		// Save state
		actorEntry.state = JSON.stringify(initRes);

		// Save actor
		this.actorRegistry.set(id, actorEntry);
	}

	async callActor({ moduleName, actorName, instanceName, fn, request, trace }: CallOpts): Promise<unknown> {
		const entry = await this.getEntry(moduleName, actorName, instanceName);
		const actor = this.constructActor(entry, false);
		actor.state = entry.state ? JSON.parse(entry.state!) : undefined;

		const context = this.createActorContext(
			moduleName,
			actorName,
			appendTraceEntry(trace, { actorCall: { module: moduleName, actor: actorName, fn } }),
		);

		// Run actor function
		let callRes = (actor as any)[fn](context, request);
		if (callRes instanceof Promise) callRes = await callRes;

		// Save state
		entry.state = JSON.stringify(actor.state);

		return callRes;
	}

	async getOrCreateAndCallActor(
		{ moduleName, actorName, instanceName, input, fn, request, trace }: GetOrCreateAndCallOpts,
	): Promise<unknown> {
		// Create actor if needed
		if (!this.actorRegistry.has(await this.getId(moduleName, actorName, instanceName))) {
			await this.createActor({ moduleName, actorName, instanceName, input, trace });
		}

		// Call actor
		const callRes = await this.callActor({ moduleName, actorName, instanceName, fn, request, trace });

		return callRes;
	}

	async actorExists({ moduleName, actorName, instanceName }: ExistsOpts): Promise<boolean> {
		return this.actorRegistry.has(await this.getId(moduleName, actorName, instanceName));
	}

	private async getId(moduleName: string, actorName: string, instanceName: string) {
		const module = this.config.modules[moduleName];
		const actor = module.actors[actorName];
		const name = `%%${module.storageAlias}%%${actor.storageAlias}%%${instanceName}`;
		return await this.hash(name);
	}

	private async getEntry(moduleName: string, actorName: string, instanceName: string): Promise<ActorEntry> {
		const entry = this.actorRegistry.get(await this.getId(moduleName, actorName, instanceName));
		if (!entry) throw new Error("Actor not created");
		return entry;
	}

	/**
	 * Creates a new instance of actor.
	 *
	 * `init` is true if calling `initialize` and `state` is undefined.
	 */
	private constructActor(entry: ActorEntry, init: boolean) {
		// Create actor instance
		if (!init && entry.state === undefined) throw Error("actor state not initiated");

		// Get actor config
		if (!(entry.moduleName in this.config.modules)) throw new Error("module not found");
		const moduleConfig = this.config.modules[entry.moduleName];
		if (!(entry.actorName in moduleConfig.actors)) throw new Error("actor not found");
		const actorConfig = moduleConfig.actors[entry.actorName];

		// TODO: cache actor instance in memory
		// Run actor function
		const actor = new (actorConfig.actor)(
			new MemoryStorage(entry),
			new MemorySchedule(this, entry),
		);

		return actor;
	}

	private createActorContext(moduleName: string, actorName: string, trace: Trace): ActorContext<ModuleContextParams> {
		// Build context
		const module = this.config.modules[moduleName];
		const context = new ActorContext<ModuleContextParams>(
			this.runtime,
			trace,
			moduleName,
			this.runtime.postgres.getOrCreatePrismaClient(this.runtime.config, module),
			module.db?.schema,
			actorName,
			this.dependencyCaseConversionMap,
			this.actorDependencyCaseConversionMap,
		);

		return context;
	}

	private async hash(input: string) {
		const data = this.encoder.encode(input);
		const hash = await crypto.subtle.digest("SHA-256", data);
		const hashString = Array.from(new Uint8Array(hash)).map((b) => b.toString(16).padStart(2, "0")).join("");

		return hashString;
	}
}

export { MemoryActorDriver as ActorDriver };

