import { RuntimeError } from "./error.ts";
import { errorToLogEntries, LogEntry } from "./logger.ts";
import { Context, ModuleContextParams } from "./mod.ts";
import { Runtime } from "./runtime.ts";

const MODULE_CALL = /^\/modules\/(?<module>\w+)\/scripts\/(?<script>\w+)\/call\/?$/;

interface RequestInfo {
	remoteAddress: string;
}

export async function handleRequest<Params extends ModuleContextParams>(
	runtime: Runtime<Params>,
	req: Request,
	info: RequestInfo,
): Promise<Response> {
	const url = new URL(req.url);

	// Create context
	const ctx = runtime.createRootContext({
		httpRequest: {
			method: req.method,
			path: url.pathname,
			remoteAddress: info.remoteAddress,
			headers: Object.fromEntries(req.headers.entries()),
		},
	});

	// Log request
	const start = performance.now();
	ctx.log.debug(
		"http request",
		["method", req.method],
		["path", url.pathname],
		["remoteAddress", info.remoteAddress],
		["userAgent", req.headers.get("user-agent")],
	);

	// Execute request
	const res = await handleRequestInner(runtime, req, url, ctx);

	// Log response
	//
	// `duration` will be 0 on Cloudflare Workers if there are no async actions
	// performed inside of the request:
	// https://developers.cloudflare.com/workers/runtime-apis/performance/
	const duration = Math.ceil(performance.now() - start);
	ctx.log.debug(
		"http response",
		["status", res.status],
		...(duration > 0 ? [["duration", `${duration}ms`] as LogEntry] : []),
	);

	return res;
}

async function handleRequestInner<Params extends ModuleContextParams>(
	runtime: Runtime<Params>,
	req: Request,
	url: URL,
	ctx: Context<Params>,
): Promise<Response> {
	// Handle CORS preflight
	if (req.method === "OPTIONS") {
		return runtime.corsPreflight(req);
	}

	// Disallow even simple requests if CORS is not allowed
	if (!runtime.corsAllowed(req)) {
		return new Response(undefined, {
			status: 403,
			headers: {
				"Vary": "Origin",
				...runtime.corsHeaders(req),
			},
		});
	}

	// Only allow POST requests
	if (req.method !== "POST") {
		return new Response(undefined, {
			status: 405,
			headers: {
				"Allow": "POST",
				...runtime.corsHeaders(req),
			},
		});
	}

	// Get module and script name
	const matches = MODULE_CALL.exec(url.pathname);
	if (!matches?.groups) {
		return new Response(
			JSON.stringify({
				"message": "Route not found. Make sure the URL and method are correct.",
			}),
			{
				headers: {
					"Content-Type": "application/json",
					...runtime.corsHeaders(req),
				},
				status: 404,
			},
		);
	}

	// Lookup script
	const moduleName = matches.groups.module;
	const scriptName = matches.groups.script;
	const script = runtime.config.modules[moduleName]?.scripts[scriptName];

	// Confirm script exists and is public
	if (!script || !script.public) {
		return new Response(
			JSON.stringify({
				"message": "Route not found. Make sure the URL and method are correct.",
			}),
			{
				headers: {
					"Content-Type": "application/json",
					...runtime.corsHeaders(req),
				},
				status: 404,
			},
		);
	}

	// Parse body
	let body: any;
	try {
		body = await req.json();
	} catch {
		const output = {
			message: "Request must have a valid JSON body.",
		};
		return new Response(JSON.stringify(output), {
			status: 400,
			headers: {
				"Content-Type": "application/json",
				...runtime.corsHeaders(req),
			},
		});
	}

	try {
		// Call module
		const output = await ctx.call(
			moduleName as any,
			scriptName as any,
			body,
		);

		if (output.__tempPleaseSeeOGBE3_NoData) {
			return new Response(undefined, {
				status: 204,
				headers: {
					...runtime.corsHeaders(req),
				},
			});
		}

		return new Response(JSON.stringify(output), {
			status: 200,
			headers: {
				"Content-Type": "application/json",
				...runtime.corsHeaders(req),
			},
		});
	} catch (error) {
		// Error response
		let output;
		if (error instanceof RuntimeError) {
			ctx.log.error(
				"runtime error",
				...errorToLogEntries("error", error),
			);
			output = {
				message: error.message,
				trace: error.trace,
			};
		} else {
			ctx.log.error("internal error", ["error", error]);
			output = {
				message: "Internal error. More details have been printed in the logs.",
				trace: error.trace,
			};
		}

		return new Response(JSON.stringify(output), {
			status: 500,
			headers: {
				"Content-Type": "application/json",
				...runtime.corsHeaders(req),
			},
		});
	}
}

src/runtime/trace.ts:
import { JsonObject } from "../types/json.ts";
import { UnreachableError } from "./error.ts";
import { BuildRuntime } from "./runtime.ts";

/**
 * Provides context about where this script call came from.
 */
export interface Trace extends JsonObject {
	rayId: string;
	entries: TraceEntry[];
}

/**
 * Single entry in the trace.
 */
export interface TraceEntry extends JsonObject {
	requestId: string;
	startedAt: string;
	type: TraceEntryType;
}

/**
 * Infomration about the type of the trace entry.
 */
export type TraceEntryType =
	| { httpRequest: TraceEntryTypeHttpRequest }
	| {
		script: TraceEntryTypeScript;
	}
	| { actorInitialize: TraceEntryTypeActorInitialize }
	| { actorCall: TraceEntryTypeActorCall }
	| { actorSchedule: TraceEntryTypeActorSchedule }
	| { test: TraceEntryTypeTest }
	| { internalTest: TraceEntryTypeInternalTest };

export function stringifyTraceEntryType(trace: TraceEntryType) {
	if ("httpRequest" in trace) {
		const { method, path } = trace.httpRequest;
		return `httpRequest(${method} ${path})`;
	} else if ("script" in trace) {
		const { module, script } = trace.script;
		return `script(${module}.${script})`;
	} else if ("actorInitialize" in trace) {
		const { module, actor } = trace.actorInitialize;
		return `actorInitialize(${module}.${actor})`;
	} else if ("actorCall" in trace) {
		const { module, actor, fn } = trace.actorCall;
		return `actorCall(${module}.${actor}.${fn})`;
	} else if ("actorSchedule" in trace) {
		return "actorSchedule";
	} else if ("test" in trace) {
		const { module, name } = trace.test;
		return `test(${module}.${name})`;
	} else if ("internalTest" in trace) {
		return "internalTest";
	} else {
		throw new UnreachableError(trace);
	}
}

export interface TraceEntryTypeHttpRequest extends JsonObject {
	method: string;
	path: string;
	remoteAddress: string;
	headers: { [key: string]: string };
}

export interface TraceEntryTypeScript extends JsonObject {
	module: string;
	script: string;
}

export interface TraceEntryTypeActorInitialize extends JsonObject {
	module: string;
	actor: string;
}

export interface TraceEntryTypeActorCall extends JsonObject {
	module: string;
	actor: string;
	fn: string;
}

export interface TraceEntryTypeActorSchedule extends JsonObject {}

export interface TraceEntryTypeTest extends JsonObject {
	module: string;
	name: string;
}

export interface TraceEntryTypeInternalTest extends JsonObject {
}

export function newTrace(entryType: TraceEntryType, runtime: BuildRuntime = BuildRuntime.Deno): Trace {
	const entry: TraceEntry = {
		requestId: crypto.randomUUID(),
		startedAt: new Date().toISOString(),
		type: entryType,
	};

	// Read managed opengb ray id from request header (set by cloudflare)
	let rayId: string;
	if (runtime == BuildRuntime.Cloudflare && "httpRequest" in entry) {
		rayId = (entry.httpRequest as TraceEntryTypeHttpRequest).headers["x-opengb-ray-id"];
	} else {
		// Generate random ray ID
		rayId = crypto.randomUUID();
	}

	return {
		rayId,
		entries: [entry],
	};
}

/**
 * Returns a new trace with the given entry appended to it.
 */
export function appendTraceEntry(
	trace: Trace,
	entryType: TraceEntryType,
): Trace {
	const entry: TraceEntry = {
		requestId: crypto.randomUUID(),
		startedAt: new Date().toISOString(),
		type: entryType,
	};

	return {
		rayId: trace.rayId,
		entries: [...trace.entries, entry],
	};
}

src/types/case_conversions.ts:
export function camelify(snake: string) {
	return snake.replace(/_([a-z])/g, (g) => g[1].toUpperCase());
}

export function pascalify(snake: string) {
	const camel = camelify(snake);
	return camel[0].toUpperCase() + camel.slice(1);
}

export function snakeify(camel: string) {
	return camel.replace(/[A-Z]/g, (g) => `_${g.toLowerCase()}`);
}

